
#include<iostream>
#include <cmath>

using namespace std;

int main(){
	short A;
	short B;
	cout << "Please enter the first number A: ";
	cin >> A;
	cout << "Please enter the second number B: ";
	cin >> B;
    cout << endl;
	cout << A  << " + " << B << " = " << A+B << endl;
	cout << A  << " - " << B << " = " << A-B << endl;
	cout << A  << " * " << B << " = " << A*B << endl;
	cout << A  << " / " << B << " = " << A/B << endl;
	cout << A  << " % " << B << " = " << A%B << endl;
	return 0;
}

